def find_xmas_in_direction(l, i, j, di, dj):
    if 0 <= i + 3 * di < len(l) and 0 <= j + 3 * dj < len(l[i]):
        if l[i][j] == 'X' and l[i + di][j + dj] == 'M' and l[i + 2 * di][j + 2 * dj] == 'A' and l[i + 3 * di][j + 3 * dj] == 'S':
            return 1
    return 0

l = inp.split()
total = 0

for i in range(len(l)):
    for j in range(len(l[i])):
        total += find_xmas_in_direction(l, i, j, 0, 1)  # Horizontal
        total += find_xmas_in_direction(l, i, j, 1, 0)  # Vertical
        total += find_xmas_in_direction(l, i, j, 1, 1)  # Diagonal down-right
        total += find_xmas_in_direction(l, i, j, 1, -1) # Diagonal down-left
        total += find_xmas_in_direction(l, i, j, 0, -1) # Horizontal reverse
        total += find_xmas_in_direction(l, i, j, -1, 0) # Vertical reverse
        total += find_xmas_in_direction(l, i, j, -1, -1) # Diagonal up-left
        total += find_xmas_in_direction(l, i, j, -1, 1) # Diagonal up-right

print(total)

inp = '''
MMMSXXMASM
MSAMXMSMSA
AMXSXMAAMM
MSAMASMSMX
XMASAMXAMM
XXAMMXXAMA
SMSMSASXSS
SAXAMASAAA
MAMMMXMMMM
MXMXAXMASX
'''

def find_x_mas_pattern(l, i, j):
    count = 0
    if 0 <= i - 1 < len(l) and 0 <= i + 1 < len(l) and 0 <= j - 1 < len(l[i]) and 0 <= j + 1 < len(l[i]):
        # Check for the pattern "M.S" in the shape of an X
        if l[i][j] == 'A' and l[i - 1][j - 1] == 'M' and l[i + 1][j + 1] == 'S' and l[i + 1][j - 1] == 'M' and l[i - 1][j + 1] == 'S':
            count += 1
        # Check for the pattern "S.M" in the shape of an X
        if l[i][j] == 'A' and l[i - 1][j - 1] == 'S' and l[i + 1][j + 1] == 'M' and l[i + 1][j - 1] == 'S' and l[i - 1][j + 1] == 'M':
            count += 1
    return count

l = inp.split()
total = 0

for i in range(1, len(l) - 1):
    for j in range(1, len(l[i]) - 1):
        total += find_x_mas_pattern(l, i, j)

print(total)